script:
frappe.ui.form.on('Sensor Group', {
	onload(frm) {
	  // your code here
	  var template = '';
	  if (frm.doc.route && frm.doc.published) {
	    template = '<h4><a style="color: #4682b4; font-size: 10pt;" href="' + frm.doc.route + '">' + frm.doc.title + '</a></h4>';
	    frm.set_df_property('html_route', 'options', frappe.render_template(template));
        frm.refresh_field('html_route');
	  }
	},
	refresh(frm) {
	  //frm.add_custom_button(__("Example Button"), function() {
      //  frappe.msgprint("Code Test");
      //}, __("iCode"));
      var template = '';
	  if (frm.doc.route && frm.doc.published && !frm.doc.__islocal) {
	    template = '<h4><a style="color: #4682b4; font-size: 10pt;" href="' + frm.doc.route + '">' + frm.doc.title + '</a></h4>';
	    frm.set_df_property('html_route', 'options', frappe.render_template(template));
        frm.refresh_field('html_route');
	  }  
	}
})

frappe.ui.form.on('Sensor', {
  setup(frm) {
    frm.set_query("dashboard_chart", function() {
        return {
          "filters": [["Dashboard Chart", "name", "=", frm.doc.name]]
        };
    });  
  },
  onload(frm) {
    show_image(frm);
  },
  refresh(frm) {
    show_image(frm);
  }
});
function show_image (frm) {
  var path = '';
  if (frm.doc.pictures) {
    frappe.call({
      method: 'pibicontrol.pibicontrol.api.get_image',
        args: {
	      slide: frm.doc.pictures
        },
        callback: (r) => {
          //console.log(r.message);
          if (r.message.length >0) {
            var rtemplate = "";
            for (var j in r.message) {
              //rtemplate += '<a href="' + r.message[j].image + '" target="_blank"><img style="border: 1px solid #4682b4; float: left; margin: 1px; padding: 1px;" height="120px" src="' + r.message[j].image + '" /></a>';  
              rtemplate += '<div class="card"><a href="' + r.message[j].image + '" target="_blank"><img class="card-img-top" style="border: 1px solid #4682b4; border-radius: 9px; float: left; margin: 1px; padding: 1px;" height="120px" title="' + r.message[j].heading + '" src="' + r.message[j].image + '" /></a></div>'; 
            }
            //var template = '<div class="container-fluid"><h4>' + frm.doc.media_library + '</h4>' + rtemplate + '</div>';
            var template = '<div class="container-fluid"><h4>Gallery</h4><div class="card-columns">' + rtemplate + '</div></div>';
            //console.log(template);
            frm.set_df_property('gallery', 'options', frappe.render_template(template));
            frm.refresh_field('gallery');
          }
        }
    });
  } else {
    //console.log(frm.doc.media_library);
    var noimage = '<div class="container-fluid"><h4>No Image on Gallery</h4></div>';
    frm.set_df_property('gallery', 'options', frappe.render_template(noimage));
    frm.refresh_field('gallery');
  }    
}
